mapper.update:
UPDATE t_mem
SET mem_delCheck = 1
WHERE mem_seq = #{memSeq} and mem_pw = #{memPw}

UPDATE t_mem SET
mem_name = #{memName},
mem_position = #{memPosition},
mem_dept = #{memDept},
mem_company = #{memCompany}
WHERE mem_seq = #{memSeq}

UPDATE t_mem
SET
    mem_image = #{memImage}
WHERE mem_seq = #{memSeq}

UPDATE t_mem
SET mem_pw = #{memPw}
WHERE mem_seq = #{memSeq}

mapper.select:
SELECT mem_delcheck
FROM t_mem
WHERE mem_email = #{memEmail}

SELECT mem_pw
FROM t_mem
WHERE mem_seq = #{memSeq}
